package com.fzc.javatest.shejimoshi.flyweight;

public class Tree {
	private int x;
	private int y;
	private int price;
	
	public Tree(int x, int y, int price) {
		// TODO Auto-generated constructor stub
		this.x = x;
		this.y = y ;
		this.price = price;
	}
	
	/**
	 * @return the x
	 */
	public int getX() {
		return x;
	}
	/**
	 * @param x the x to set
	 */
	public void setX(int x) {
		this.x = x;
	}
	/**
	 * @return the y
	 */
	public int getY() {
		return y;
	}
	/**
	 * @param y the y to set
	 */
	public void setY(int y) {
		this.y = y;
	}
	/**
	 * @return the price
	 */
	public int getPrice() {
		return price;
	}
	/**
	 * @param price the price to set
	 */
	public void setPrice(int price) {
		this.price = price;
	}

	public void display() {
		// TODO Auto-generated method stub
//		System.out.println("");
	}
	
	
	
}
